import styled from "styled-components";

export const StyledButton = styled("button")<any>`
  background: ${(p) => p.color || "#2590FE"};
  color: ${(p) => (p.color ? "#0C2641" : "#fff")};
  font-size: 1rem;
  font-weight: 400;
  width: 100%;
  border: 0px solid #2590FE;
  border-radius: 4px;
  padding: 13px 0;
  cursor: pointer;
  margin-top: 0.625rem;
  max-width: 180px;
  transition: all 0.3s ease-in-out;
  box-shadow: 0 16px 30px rgb(23 31 114 / 20%);
  
  &:hover,
  &:active,
  &:focus {
    color: #fff;
    border: 0px solid #3894E0;
    background-color: #3894E0;
  }
`;
